import React, { useEffect, useReducer } from 'react'



const reducerCity = (state, { type, city }) => {
    switch (type) {
        case 'TYPE_CITY':
            const cityName = city.target.value
            return cityName

        default: return state
    }
}
const reducerShowWeather = (state, { type, weatherDetail }) => {
    switch (type) {
        case 'SHOW_WEATHER':
            return weatherDetail
        default: return state

    }
}

const reducerSelCity = (state, { type, payload }) => {
    switch (type) {
        case 'SELECT_CITY':

            return payload

        default: return state
    }
}
const Weather = () => {

    const [inputType, dispatchCity] = useReducer(reducerCity, '')
    const [selectedCity, dispatchSelCity] = useReducer(reducerSelCity, 'delhi')
    const [weatherData, dispatchWeather] = useReducer(reducerShowWeather, '')

    const weatherAPIData = async () => {
        try {
            let url = `https://api.openweathermap.org/data/2.5/weather?q=${selectedCity}&units=metric&appid=2d09fd0d294278c0015aecacf7df42dd`

            const res = await fetch(url);
            const data = await res.json();
            console.log(data.main.temp)
            dispatchWeather({ type: 'SHOW_WEATHER', weatherDetail: data.main.temp })
        } catch (error) {
            console.log(error)
        }
    }
    useEffect(() => {
        weatherAPIData();
    }, [])

    return (
        <>
            <h3>Weather Report</h3>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-12 col-md-4 col-sm-12 col-xs-12">
                        <div className="card-weather p-4">
                            <div className="search-wrap">
                                <div className="input-group mb-3">
                                    <input type='text' name='city' value={inputType} onChange={(e) => dispatchCity({ type: 'TYPE_CITY', city: e })} />
                                    <div className="input-group-append">
                                        <button className="btn btn-danger" type="submit" onClick={() => dispatchSelCity({ type: 'SELECT_CITY', payload: inputType })}>Go</button>
                                    </div>
                                </div>
                            </div>
                            <div className="d-flex">
                                <h6 className="flex-grow-1">London</h6>
                                <h6>16:08</h6>
                            </div>
                            <div className="d-flex flex-column temp mt-5 mb-3">
                                <h1 className="mb-0 font-weight-bold" id="heading"> {weatherData}Â° C </h1> <span className="small grey">Stormy</span>
                            </div>
                            <div className="d-flex">
                                <div className="temp-details flex-grow-1">
                                    <p className="my-1"> <img src="https://i.imgur.com/B9kqOzp.png" height="17px" alt="weather" /> <span> 40 km/h </span> </p>
                                    <p className="my-1"> <i className="fa fa-tint mr-2" aria-hidden="true"></i> <span> 84% </span> </p>
                                    <p className="my-1"> <img src="https://i.imgur.com/wGSJ8C5.png" height="17px" alt="weather" /> <span> 0.2h </span> </p>
                                </div>
                                <div> <img src="https://i.imgur.com/Qw7npIg.png" width="100px" alt="weathers" /> </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Weather
